import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./App.css";


const App = () => {
  const [quotes, setQuotes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState(null);

  useEffect(() => {
    setLoading(true);
    fetch("http://127.0.0.1:8000/api/quotes")
    .then(response => {
      setLoading(false);
      return response.json();
    })
    .then(quotes => setQuotes(quotes), errors => setErrors(errors));
    // .then(quotes => setQuotes(quotes.message), errors => setErrors(errors));
  }, []);

  const navigate = useNavigate();

  if(!sessionStorage.getItem('access-token')) {
    return navigate("/login");
  }

  return <div>
    {loading && <div>Fetching Simpson Quotes...</div>}
    {errors && JSON.stringify(errors)}
    {quotes && quotes.length > 0 && (
      <div>
        {
          // quotes.map((quote, index) => { return <div key={index}>{quote.quote}</div> })
          quotes.map((quote, index) => { return <img alt="" key={index} src={quote.image}/> })
        }
      </div>
    )}
  </div>

}

export default App;
